package assignment.SuperStore;public class Address {    private String houseNumber;    private String streetName;    private String city;    private String state;    private String country;    public Address() {        this(null, null, null, null, null);    }    public Address(String houseNumber, String streetName, String city, String state, String country) {        validateHouseNumber(houseNumber);        this.houseNumber = houseNumber;        this.streetName = streetName;        this.city = city;        this.state = state;        this.country = country;    }    private void validateHouseNumber(String houseNumber) {        boolean isInvalid = houseNumber.isEmpty();        if(isInvalid) throw new IllegalArgumentException("House number cannot be empty");    }}